        public static bool IsEmailRFC822(string email) => Regex.IsMatch(email, RFC822Regex);

        public static bool IsPhone(string phone) => Regex.IsMatch(phone, @"^1[3456789]\d{9}$");

        public static bool IsIDCardNo(string IDcardNo)
        {
            bool result;
            try
            {
                if (IDcardNo.Length != 18)
                {
                    return false;
                }

                if (long.TryParse(IDcardNo.Remove(17), out long n) == false || n < Math.Pow(10, 16) || long.TryParse(IDcardNo.Replace('x', '0').Replace('X', '0'), out n) == false)
                {
                    return false;//数字验证  
                }
                string address = "11x22x35x44x53x12x23x36x45x54x13x31x37x46x61x14x32x41x50x62x15x33x42x51x63x21x34x43x52x64x65x71x81x82x91";
                if (!address.Contains(IDcardNo.Remove(2), StringComparison.CurrentCulture))
                {
                    return false;//省份验证  
                }

                string birth = IDcardNo.Substring(6, 8).Insert(6, "-").Insert(4, "-");
                DateTime time = new();
                if (DateTime.TryParse(birth, out time) == false)
                {
                    return false;//生日验证  
                }

                string[] arrVarifyCode = "1,0,x,9,8,7,6,5,4,3,2".Split(',');
                string[] Wi = "7,9,10,5,8,4,2,1,6,3,7,9,10,5,8,4,2".Split(',');
                char[] Ai = IDcardNo.Remove(17).ToCharArray();
                int sum = 0;
                for (int i = 0; i < 17; i++)
                {
                    sum += int.Parse(Wi[i]) * int.Parse(Ai[i].ToString());
                }
                int y = -1;
                Math.DivRem(sum, 11, out y);
                if (string.Equals(arrVarifyCode[y] ,IDcardNo.Substring(17, 1).ToLower(CultureInfo.CurrentCulture),StringComparison.Ordinal))
                {
                    return false;//校验码验证  
                }
                return true;//符合GB11643-1999标准 
            }
            catch
            {
                result = false;
            }
            return result;
        }

        public static bool IsURL(string url) => Regex.IsMatch(url, @"(https?://(w{3}\\.)?)?\\w+\\.\\w+(\\.[a-zA-Z]+)*(:\\d{1,5})?(/\\w*)*(\\??(.+=.*)?(&.+=.*)?)?");

        public static bool IsIPv4(string ipAddress) => Regex.IsMatch(ipAddress, @"^((25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$");

        public static bool IsIPv4Net(string ipAddress) => IPAddress.TryParse(ipAddress, out IPAddress parsedIpAddress) && parsedIpAddress.AddressFamily == System.Net.Sockets.AddressFamily.InterNetwork;

        public static bool IsColorHex(string str) => Regex.IsMatch(str, @"#([a-fA-F0-9]{3,6})");

        public static bool IsExistProtocol(string str) => Regex.IsMatch(str, @"[a-zA-Z]+:\/\/");

    }
